 INSERT INTO movies (id, name, original_name, year_of, country, duration, description, poster_id) VALUES (263775, "Удар по семье", "Stangata in famiglia", 1976, Италия, STR_TO_DATE(1:30,"%H:%i"), "Вдовец Брамбилла служит фининспектором в государственном управлении. На свое небольшое жалование он содержит двух своих дочерей, а также безработного брата и его дочь. Чтобы поправить свое финансовое положение, он принимает предложение провести независимое частное расследование, объектом которого стала проституция, замаскированная под массажные и косметические салоны. Дав объявление о наборе штата для такого салона, Брамбилла стал ждать результатов. Вот только поскольку все дело происходило у него дома, то свидетелями этих результатов стали и его домочадцы, которые решили продолжить эксперимент уже за спиной у почтенного фининспектора…", 263775);
