 INSERT INTO movies (id, name, original_name, year_of, country, duration, description, poster_id) VALUES (263782, "Балканский экспресс 2", "Balkan ekspres 2", 1989, Югославия, STR_TO_DATE(1:48,"%H:%i"), "Действие происходит в годы Второй мировой войны. Группа мошенников промышляет карманными кражами. С началом войны ловкачам приходится прекратить свою деятельность. Бывшие мошенники организуют оркестр для работы в ресторане и начинают выполнять задания подпольщиков. Приключения пятёрки друзей продолжаются...", 263782);
